function main(fileName)
    local file = io.open(fileName, "r");
    local number = 0;

    for i in (file:read("*all")):gmatch("%S+") do
        number = number + tonumber(i);
    end

    file:close();
    return number;
end

print(main("2018/1/input.txt"));

